mapper.select:
/* FaqImgMapper.getList */
SELECT
	fi.*
FROM faq_img fi
WHERE fi.faq_seq = #{faqSeq}
AND fi.del_yn = 'N'

mapper.insert:
/* FaqImgMapper.insert */
INSERT INTO faq_img
(
	 faq_seq
	,faq_img
	,del_yn
	,img_nm
	,img_title
	,rgstr_id
	,rgst_dt
)
VALUES
(
	 #{faqSeq}
	,#{faqImg}
	,'N'
	,#{imgNm}
	,#{imgTitle}
	,#{rgstrId}
	,SYSDATE()
)

mapper.update:
/* FaqImgMapper.update */
UPDATE faq_img
SET
	 faq_seq 	= 	#{faqSeq}
	,img_title  =	#{imgTitle}
	,faq_img	= 	#{faqImg}
	,img_nm		=	#{imgNm}
	,modr_id	=	#{modrId}
	,mod_dt		= 	SYSDATE()
WHERE img_seq 	=	#{imgSeq}

/* FaqImgMapper.update */
UPDATE faq_img SET
	 del_yn = 'Y'
	,modr_id	=	#{modrId}
	,mod_dt		= 	SYSDATE()
WHERE faq_seq = #{faqSeq}

/* FaqImgMapper.delete */
UPDATE faq_img SET
	 del_yn = 'Y'
	,modr_id	=	#{modrId}
	,mod_dt		= 	SYSDATE()
WHERE img_seq = #{imgSeq}
