mapper.select:
/* FaqImgMapper.getList */
SELECT
	fi.*
FROM faq_img fi
WHERE fi.faq_seq = #{faqSeq}
AND fi.del_yn = 'N'

mapper.insert:
/* FaqImgMapper.insert */
INSERT INTO faq_img
(
	 faq_seq
	,faq_img
	,del_yn
	,img_nm
)
VALUES
(
	 #{faqSeq}
	,#{faqImg}
	,'N'
	,#{imgNm}
)

mapper.update:
/* FaqImgMapper.update */
DELETE FROM faq_img
WHERE faq_seq = #{faqSeq}

/* FaqImgMapper.delete */
UPDATE faq_img SET
	del_yn = #{delYn}
WHERE img_seq = #{imgSeq}
