mapper.select:
/* FaqMapper.getList */
SELECT
	f.*
FROM faq f
WHERE f.faq_del_yn = 'N'
AND f.faq_seq = #{faqSeq}

mapper.insert:
/* FaqMapper.insert */
INSERT INTO FAQ
(
	 faq_title
	,faq_content
	,faq_del_yn
	,faq_type
	,rgstr_id
	,rgst_dt
)
VALUES
(
	#{faqTitle}
	,#{FaqContent}
	,'N'
	,#{FaqType}
	,#{rgstrId}
	,SYSDATE()
)

mapper.update:
/* FaqMapper.update */
update faq set
	 faq_title 		= #{faqTitle}
	,faq_content 	= #{faqContent}
	,modr_id	=	#{modrId}
	,mod_dt		= 	SYSDATE()
where faq_seq 		= #{faqSeq}

/* FaqMapper.update */
update faq set
	 faq_del_yn		= 'Y'
	,modr_id	=	#{modrId}
	,mod_dt		= 	SYSDATE()
where faq_seq 		= #{faqSeq}
